% Create multiple error bars using matrix input to errorbar
errorbar(xvector1,ymatrix1,DMatrix1,'LineStyle','none','LineWidth',1,...
    'Color',[0 0 0]);

% Create ylabel
ylabel('Walk (%)','FontSize',18,'Interpreter','none');

